#!/usr/bin/python3 -V
# -*- coding: utf-8 -*-correspondence_table = [["I", "II", "III", "IV", "V", "VI", "VII", "VIII", "IX"],                        ["X", "XX", "XXX", "XL", "L", "LX", "LXX", "LXXX", "XC"],                        ["C", "CC", "CCC", "CD", "D", "DC", "DCC", "DCCC", "CM"],                        ["M", "MM", "MMM"],]def numeral(arabic):    ans = ""    y = 0    for x in str(arabic)[::-1]:        if x == "0":            y += 1            continue        else:            ans = correspondence_table[y][int(x) - 1] + ans            y += 1    return ans
